mapper.update:
UPDATE ORDER_DETAIL
SET STATUS = 'Y'
WHERE ORDER_NO = #{orderNo}
	AND PRODUCT_SEQ = #{productSeq}

mapper.select:
SELECT COUNT(VR_SEQ) AS COUNT
  FROM VOTE_UPLOAD
 WHERE USER_ID = #{value}

SELECT VR_SEQ, VL_USER_ID, USER_ID, USER_NAME, CATEGORY_NO, CATEGORY_NAME, VR_TITLE, VR_CONTENT, VR_TOTAL_CNT, VR_START_PRICE, STATUS, REG_DATE, VR_START_DATE, VR_END_DATE FROM ( SELECT ROWNUM AS RNUM, VR_SEQ, VL_USER_ID, USER_ID, USER_NAME, CATEGORY_NO, CATEGORY_NAME, VR_TITLE, VR_CONTENT, VR_TOTAL_CNT, VR_START_PRICE, STATUS, REG_DATE, VR_START_DATE, VR_END_DATE FROM ( SELECT VL.VR_SEQ, NVL(VL.VL_USER_ID,'') AS VL_USER_ID, NVL(VU.USER_ID, '') AS USER_ID, NVL(A.USER_NAME,'') AS USER_NAME, NVL(VU.CATEGORY_NO, 00) AS CATEGORY_NO, NVL(C.CATEGORY_NAME, '') AS CATEGORY_NAME, NVL(VR_TITLE, '') AS VR_TITLE, NVL(VR_CONTENT, '') AS VR_CONTENT, NVL(VR_TOTAL_CNT, 0) AS VR_TOTAL_CNT, NVL(VR_START_PRICE, 0) AS VR_START_PRICE, NVL(VU.STATUS, 'N') AS STATUS, NVL(TO_CHAR(REG_DATE, 'YYYY.MM.DD HH24:MI:SS'), '') AS REG_DATE, NVL(VR_START_DATE, '') AS VR_START_DATE, NVL(VR_END_DATE, '') AS VR_END_DATE FROM VOTE_UPLOAD VU, VOTE_LIST VL, A_USER A, CATEGORY C WHERE VL.VL_USER_ID = #{userId} AND VU.VR_SEQ = VL.VR_SEQ(+) AND VU.USER_ID = A.USER_ID AND VU.CATEGORY_NO = C.CATEGORY_NO
AND VU.CATEGORY_NO = #{categoryNo}
ORDER BY REG_DATE DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT COUNT(VL_USER_ID) AS COUNT
  FROM VOTE_LIST VL, VOTE_UPLOAD VU, CATEGORY C
 WHERE VL_USER_ID = #{vlUserId}
   AND VL.VR_SEQ = VU.VR_SEQ
   AND VU.CATEGORY_NO = C.CATEGORY_NO

   AND C.CATEGORY_NO = #{categoryNo}
